/** A File or Blob object or a URL string */
export declare type FileInput = File | Blob | string;
export interface FileInfo {
    path: string;
    name: string;
    ext: string;
    base: string;
    dir: string;
    compressed: string | boolean;
    binary: boolean;
    protocol: string;
    query: string;
    src: FileInput;
}
export declare function getFileInfo(file: FileInput): FileInfo;
